import React from 'react';

import src1 from '../assets/img/src1.jpg'
import src2 from '../assets/img/src2.jpg'
import src3 from '../assets/img/src3.jpg'

class Carousel extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    title: 'Title1',
                    subTitle: 'something',
                    imgSrc: src1,
                    link: 'www.google.com',
                    selected: false
                },
                {
                    id: 1,
                    title: 'Title2',
                    subTitle: 'something',
                    imgSrc: src2,
                    link: 'www.google.com',
                    selected: false
                },
                {
                    id: 2,
                    title: 'Title3',
                    subTitle: 'something',
                    imgSrc: src3,
                    link: 'www.google.com',
                    selected: false
                }
            ]
        }
    }

    handleCardClick = (id, card) => {
        let items = [...this.state.items];
        items.id.selected = items.id.selected ? false : true;
        items.forEach(item => {
            if(item.id !== id) {
                item.selected = false;
            }
        });

        this.setState({
            items
        })
    }

    render(){
        return(
            <p>Carousel works!</p>
        )
    }

}

export default Carousel;
